CREATE TABLE MEET (ID INTEGER AUTO_INCREMENT NOT NULL, date DATE, description VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE COMPETITOR (ID INTEGER AUTO_INCREMENT NOT NULL, category VARCHAR(255), name VARCHAR(255), number VARCHAR(255), team VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE meet_TEAMS (meet_ID INTEGER, TEAMS VARCHAR(255))
CREATE TABLE MEET_COMPETITOR (meet_ID INTEGER NOT NULL, competitors_ID INTEGER NOT NULL, PRIMARY KEY (meet_ID, competitors_ID))
CREATE TABLE competitor_COMPETITORRESULTS (competitor_ID INTEGER, COMPETITORRESULTS DOUBLE, COMPETITORRESULTS_KEY INTEGER)
ALTER TABLE meet_TEAMS ADD CONSTRAINT FK_meet_TEAMS_meet_ID FOREIGN KEY (meet_ID) REFERENCES MEET (ID)
ALTER TABLE MEET_COMPETITOR ADD CONSTRAINT FK_MEET_COMPETITOR_meet_ID FOREIGN KEY (meet_ID) REFERENCES MEET (ID)
ALTER TABLE MEET_COMPETITOR ADD CONSTRAINT FK_MEET_COMPETITOR_competitors_ID FOREIGN KEY (competitors_ID) REFERENCES COMPETITOR (ID)
ALTER TABLE competitor_COMPETITORRESULTS ADD CONSTRAINT FK_competitor_COMPETITORRESULTS_competitor_ID FOREIGN KEY (competitor_ID) REFERENCES COMPETITOR (ID)
